package Factories;import FlourSweets.Cookies;import FlourSweets.Wafers;import Sweet.*;/** * The type Sweet factory. */public class SweetFactory {    /**     * Get factory sweet factory.     *     * @param type the type     * @return the sweet factory     */    static public SweetFactory getFactory(FactoryType type){        if (type == FactoryType.SugarSweatsFactory)            return new SugarSweetsFactory();        else            return new FlourSweetsFactory();    }}